import React, { useState, useEffect } from 'react';
import ProviderRegistration from './components/ProviderRegistration';
import ProviderLogin from './components/ProviderLogin';
import Navigation from './components/Navigation';

function App() {
  const [currentView, setCurrentView] = useState<'login' | 'registration'>('login');

  // Handle URL parameters on component mount
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const viewParam = urlParams.get('view');

    if (viewParam === 'registration') {
      setCurrentView('registration');
    } else if (viewParam === 'login') {
      setCurrentView('login');
    }
  }, []);

  const handleViewChange = (view: 'login' | 'registration') => {
    setCurrentView(view);
    // Update URL without page reload
    const newUrl = `/?view=${view}`;
    window.history.pushState({ view }, '', newUrl);
  };

  return (
    <div className="min-h-screen bg-medical-gray">
      <Navigation currentView={currentView} onViewChange={handleViewChange} />
      {currentView === 'login' ? (
        <ProviderLogin onNavigateToRegistration={() => handleViewChange('registration')} />
      ) : (
        <ProviderRegistration onNavigateToLogin={() => handleViewChange('login')} />
      )}
    </div>
  );
}

export default App;
